  const [currentScreen, setCurrentScreen] = useState&lt;Screen&gt;('dashboard');
  const [selectedTargetId, setSelectedTargetId] = useState&lt;string | null&gt;(null);

  // NavegaciÃ³n condicional basada en autenticaciÃ³n
