    case 'dashboard':
      return &lt;DashboardScreen onNavigateToDetails={navigateToDetails} /&gt;;
    case 'systems':
      return &lt;SystemsScreen onNavigateToDetails={navigateToDetails} /&gt;;
    // ...
  }
};

